# -*- coding:utf8 -*-
import json
from category import Category


def decode_category_json(json_str):
    category_json = json.loads(json_str)
    category_data = category_json["content"]["data"]
    categories = []
    for cate_item in category_data:
        cat = Category(cate_item[0], cate_item[1], cate_item[6], cate_item[2])
        categories.append(cat)
    return categories

